                oldComm = $scope.oldSnippet.previous_comments;
                newComm = response.result.previous_comments;
                var eq = true;
                if (oldComm.length != newComm.length) {
                    eq = false;
                }
                console.log("are they equal", eq, oldComm, newComm);
